    N = i

    cnt = 0
    while True:

        if N == 1:
            dp_dict[i] = cnt
            break

        if N % 3 == 0:
            print("divide 3")
            cnt += 1
            tmp_num = N
            N = N // 3
            minus_cnt = dp_dict[tmp_num - 1]
